// src/App.js
import React, { useState } from "react";
import "./App.css";
import { Howl, Howler } from "howler";

const songs = [
  {
    id: 1,
    title: "Billie Jean",
    plays: "1,040,811,084",
    duration: "4:53",
    album: "Thriller 25 Super Deluxe Edition",
    src: "/assets/billie_jean.mp3",
  },
  {
    id: 2,
    title: "Beat It",
    plays: "643,786,045",
    duration: "4:18",
    album: "Thriller 25 Super Deluxe Edition",
    src: "/assets/beat_it.mp3",
  },
  {
    id: 3,
    title: "Smooth Criminal",
    plays: "407,234,004",
    duration: "4:17",
    album: "Thriller 25 Super Deluxe Edition",
    src: "/assets/smooth_criminal.mp3",
  },
  {
    id: 4,
    title: "Don't Stop 'Til You Get Enough",
    plays: "316,391,952",
    duration: "6:05",
    album: "Bad 25th Anniversary Edition",
    src: "/assets/dont_stop.mp3",
  },
  {
    id: 5,
    title: "Rock With You",
    plays: "268,187,218",
    duration: "3:40",
    album: "Off The Wall",
    src: "/assets/rock_with_you.mp3",
  },
];

function App() {
  const [currentSong, setCurrentSong] = useState(songs[1]);
  const [isPlaying, setIsPlaying] = useState(false);
  const [howlerInstance, setHowlerInstance] = useState(null);

  const playSong = (song) => {
    if (howlerInstance) howlerInstance.stop(); // Stop any playing song
    const sound = new Howl({ src: [song.src], html5: true });
    setHowlerInstance(sound);
    sound.play();
    setCurrentSong(song);
    setIsPlaying(true);
  };

  const togglePlayPause = () => {
    if (!howlerInstance) return;
    if (isPlaying) {
      howlerInstance.pause();
    } else {
      howlerInstance.play();
    }
    setIsPlaying(!isPlaying);
  };

  const playNext = () => {
    const currentIndex = songs.findIndex((s) => s.id === currentSong.id);
    const nextIndex = (currentIndex + 1) % songs.length;
    playSong(songs[nextIndex]);
  };

  const playPrevious = () => {
    const currentIndex = songs.findIndex((s) => s.id === currentSong.id);
    const prevIndex = (currentIndex - 1 + songs.length) % songs.length;
    playSong(songs[prevIndex]);
  };

  return (
    <div className="app">
      <div className="sidebar">
        <h1>DreamMusic</h1>
        <ul>
          <li>Home</li>
          <li>Trends</li>
          <li>Library</li>
          <li>Discover</li>
        </ul>
      </div>
      <div className="main-content">
        <header>
          <h2>Michael Jackson</h2>
          <p>27,852,501 monthly listeners</p>
        </header>
        <section className="songs">
          <h3>Popular</h3>
          <table>
            <thead>
              <tr>
                <th>#</th>
                <th>Title</th>
                <th>Plays</th>
                <th>Time</th>
                <th>Album</th>
              </tr>
            </thead>
            <tbody>
              {songs.map((song, index) => (
                <tr
                  key={song.id}
                  className={song.id === currentSong.id ? "active" : ""}
                  onClick={() => playSong(song)}
                >
                  <td>{index + 1}</td>
                  <td>{song.title}</td>
                  <td>{song.plays}</td>
                  <td>{song.duration}</td>
                  <td>{song.album}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>
      </div>
      <div className="player-controls">
        <h4>Now Playing</h4>
        <p>{currentSong.title}</p>
        <div className="controls">
          <button onClick={playPrevious}>⏮</button>
          <button onClick={togglePlayPause}>
            {isPlaying ? "⏸" : "▶️"}
          </button>
          <button onClick={playNext}>⏭</button>
        </div>
      </div>
    </div>
  );
}

export default App;
