// src/App.js
import React, { useState, useRef } from "react";
import "./App.css";
import artistImage from "./michael_jackson.jpg";
import songFile from "./assets/beat_it.mp3"; // Replace this with actual song URLs or files

function App() {
  const songs = [
    {
      id: 1,
      title: "Billie Jean",
      plays: "1,040,811,084",
      time: "4:53",
      album: "Thriller",
      audio: "./assets/billie_jean.mp3", // Replace with correct path
    },
    {
      id: 2,
      title: "Beat It",
      plays: "643,786,045",
      time: "4:18",
      album: "Thriller",
      audio: "./assets/beat",
    },
    {
      id: 3,
      title: "Smooth Criminal",
      plays: "590,223,084",
      time: "4:17",
      album: "Bad",
      audio: "./assets/smooth_criminal.mp3", // Replace with correct path
    },
    {
      id: 4,
      title: "Thriller",
      plays: "712,122,300",
      time: "5:57",
      album: "Thriller",
      audio: "./assets/thriller.mp3", // Replace with correct path
    },
    {
      id: 5,
      title: "Man in the Mirror",
      plays: "479,540,932",
      time: "5:19",
      album: "Bad",
      audio: "./assets/man_in_the_mirror.mp3", // Replace with correct path
    },
  ];

  const [currentSongIndex, setCurrentSongIndex] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);

  const audioRef = useRef(new Audio(songs[currentSongIndex].audio));

  const handlePlayPause = () => {
    if (isPlaying) {
      audioRef.current.pause();
    } else {
      audioRef.current.play();
    }
    setIsPlaying(!isPlaying);
  };

  const handleNext = () => {
    const nextIndex = (currentSongIndex + 1) % songs.length;
    audioRef.current.pause();
    audioRef.current = new Audio(songs[nextIndex].audio);
    setCurrentSongIndex(nextIndex);
    setIsPlaying(true);
    audioRef.current.play();
  };

  const handlePrevious = () => {
    const prevIndex =
      currentSongIndex === 0 ? songs.length - 1 : currentSongIndex - 1;
    audioRef.current.pause();
    audioRef.current = new Audio(songs[prevIndex].audio);
    setCurrentSongIndex(prevIndex);
    setIsPlaying(true);
    audioRef.current.play();
  };

  return (
    <div id="app">
      {/* Sidebar */}
      <div className="sidebar">
        <div>
          <div className="logo">DreamMusic</div>
          <div className="menu">
            <a href="#">Home</a>
            <a href="#">Trends</a>
            <a href="#">Library</a>
            <a href="#">Discover</a>
          </div>
        </div>
        <div className="general">
          <a href="#">Settings</a>
          <a href="#">Log Out</a>
        </div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Navbar */}
        <div className="navbar">
          <div>
            <a href="#">Music</a>
            <a href="#">Podcast</a>
            <a href="#">Live</a>
            <a href="#">Radio</a>
          </div>
          <input type="text" placeholder="Search" />
        </div>

        {/* Artist Section */}
        <div className="artist-section">
          <div className="info">
            <h2>Michael Jackson</h2>
            <p>27,852,501 monthly listeners</p>
          </div>
          <img src={artistImage} alt="Michael Jackson" />
        </div>

        {/* Song List */}
        <div className="song-list">
          <table>
            <thead>
              <tr>
                <th>#</th>
                <th>Title</th>
                <th>Plays</th>
                <th>Time</th>
                <th>Album</th>
              </tr>
            </thead>
            <tbody>
              {songs.map((song, index) => (
                <tr
                  key={song.id}
                  style={{
                    backgroundColor:
                      index === currentSongIndex ? "#ff4141" : "",
                  }}
                >
                  <td>{song.id}</td>
                  <td>{song.title}</td>
                  <td>{song.plays}</td>
                  <td>{song.time}</td>
                  <td>{song.album}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Vertical Division */}
      <div className="right-panel">
        {/* Play Control Card */}
        <div className="control-card">
          <h3>Now Playing</h3>
          <p>{songs[currentSongIndex].title}</p>
          <div className="buttons">
            <button onClick={handlePrevious}>⏮ Previous</button>
            <button onClick={handlePlayPause}>
              {isPlaying ? "⏸ Pause" : "▶ Play"}
            </button>
            <button onClick={handleNext}>⏭ Next</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
