// src/App.js
import React, { useState } from "react";
import "./App.css";
import artistImage from "./assets/michael_jackson.jpg";

function App() {
  // Song data
  const songs = [
    { id: 1, title: "Billie Jean", plays: "1,040,811,084", time: "4:53", album: "Thriller" },
    { id: 2, title: "Beat It", plays: "643,786,045", time: "4:18", album: "Thriller" },
    { id: 3, title: "Smooth Criminal", plays: "590,223,084", time: "4:17", album: "Bad" },
    { id: 4, title: "Thriller", plays: "712,122,300", time: "5:57", album: "Thriller" },
    { id: 5, title: "Man in the Mirror", plays: "479,540,932", time: "5:19", album: "Bad" },
  ];

  // State for the current song and whether it's playing
  const [currentSongIndex, setCurrentSongIndex] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);

  // Handlers
  const handlePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  const handleNext = () => {
    setCurrentSongIndex((prevIndex) => (prevIndex + 1) % songs.length); // Loop to the first song
    setIsPlaying(true); // Automatically play the next song
  };

  const handlePrevious = () => {
    setCurrentSongIndex((prevIndex) =>
      prevIndex === 0 ? songs.length - 1 : prevIndex - 1
    ); // Loop to the last song
    setIsPlaying(true); // Automatically play the previous song
  };

  return (
    <div id="app">
      {/* Sidebar */}
      <div className="sidebar">
        <div>
          <div className="logo">DreamMusic</div>
          <div className="menu">
            <a href="#">Home</a>
            <a href="#">Trends</a>
            <a href="#">Library</a>
            <a href="#">Discover</a>
          </div>
        </div>
        <div className="general">
          <a href="#">Settings</a>
          <a href="#">Log Out</a>
        </div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Navbar */}
        <div className="navbar">
          <div>
            <a href="#">Music</a>
            <a href="#">Podcast</a>
            <a href="#">Live</a>
            <a href="#">Radio</a>
          </div>
          <input type="text" placeholder="Search" />
        </div>

        {/* Artist Section */}
        <div className="artist-section">
          <div className="info">
            <h2>Michael Jackson</h2>
            <p>27,852,501 monthly listeners</p>
          </div>
          <img src={artistImage} alt="Michael Jackson" />
        </div>

        {/* Song List */}
        <div className="song-list">
          <table>
            <thead>
              <tr>
                <th>#</th>
                <th>Title</th>
                <th>Plays</th>
                <th>Time</th>
                <th>Album</th>
              </tr>
            </thead>
            <tbody>
              {songs.map((song, index) => (
                <tr
                  key={song.id}
                  style={{
                    backgroundColor: index === currentSongIndex ? "#ff4141" : "",
                  }}
                >
                  <td>{song.id}</td>
                  <td>{song.title}</td>
                  <td>{song.plays}</td>
                  <td>{song.time}</td>
                  <td>{song.album}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Now Playing */}
        <div className="now-playing">
          <img src={artistImage} alt="Now Playing" />
          <div className="controls">
            <p>Now Playing: {songs[currentSongIndex].title}</p>
            <input type="range" min="0" max="100" />
          </div>
          <div className="buttons">
            <button onClick={handlePrevious}>⏮</button>
            <button onClick={handlePlayPause}>
              {isPlaying ? "⏸ Pause" : "▶ Play"}
            </button>
            <button onClick={handleNext}>⏭</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
